import React,{useState,useCallback} from 'react';
function Location() {
  const [ latlng, setLatLng ] = useState('Lat: 0.0 Lng: 0.0'); 
  const onChangeLatLngInput = useCallback(
    (e) => {
      setLatLng(e.target.value);
    },
    [latlng]
  );

  return (
    <p onChange={onChangeLatLngInput}><b>Globe Coordinates:</b>{latlng}</p>
  );
}
export default Location;
