import React from 'react';
function Button(props) {

  function changeColors() {
    alert(props.colors + " ... " + props.colorscale);
    if( props.colorscale.current === "Rainbow" ) {
      setColorScale("Grayscale");       
    } else {
      setColorScale("Rainbow");
    } 

  }

  return (
    <button onClick={changeColors}>{props.colorscale}</button>
  );
}
export default Button;
